const { Pool } = require('pg');

const pool = new Pool({
    user: 'postgres', // użytkownik bazy danych
    host: 'localhost', // adres lokalny serwera
    database: 'postgres', // nazwa bazy danych
    password: 'admin',
    port: 5432,
});

module.exports = pool;
